DROP FUNCTION IF EXISTS `fn_capitalize`;
delimiter ;;
CREATE FUNCTION `fn_capitalize`(input varchar(255))
 RETURNS varchar(255) CHARSET latin1 COLLATE latin1_swedish_ci
  DETERMINISTIC
BEGIN
    DECLARE len INT;
    DECLARE i INT;
    SET len   = CHAR_LENGTH(input);
    SET input = LOWER(input);
    SET i = 0;

    WHILE (i < len) DO
      IF (MID(input,i,1) = ' ' OR i = 0) THEN
        IF (i < len) THEN
          SET input = CONCAT(
            LEFT(input,i),
            UPPER(MID(input,i + 1,1)),
            RIGHT(input,len - i - 1)
          );
        END IF;
      END IF;
      SET i = i + 1;
    END WHILE;
    RETURN input;
  END
;;
delimiter ;

-- ----------------------------
-- Function structure for fn_create_number
-- ----------------------------
DROP FUNCTION IF EXISTS `fn_create_number`;
delimiter ;;
CREATE FUNCTION `fn_create_number`(vTABLE VARCHAR(255),
  vTYPE INT(5),
  vDATE DATE)
 RETURNS varchar(255) CHARSET latin1 COLLATE latin1_swedish_ci
  DETERMINISTIC
BEGIN
    DECLARE mNUMBER VARCHAR(255);
    DECLARE mLAST_NUMBER INT(255);
    DECLARE mRESULT VARCHAR(255);
    
      /* Search Table */
      IF vTABLE = 'trans' THEN
          SELECT IFNULL(MAX(RIGHT(`trans_number`,5)),0) INTO mLAST_NUMBER
          FROM trans
          WHERE DATE_FORMAT(trans_date_created, '%Y%m')=DATE_FORMAT(vDATE,'%Y%m')
          AND trans_type=vTYPE;
      END IF; 

      /* Generate Number */
    IF mLAST_NUMBER = 0 THEN
      SET @mNUMBER := '00001';
    ELSE
      SET mNUMBER := mLAST_NUMBER + 1; 	
      SELECT LPAD(mNUMBER,5,0) INTO @mNUMBER;
    END IF;
    SET mRESULT = CONCAT('INV-',DATE_FORMAT(vDATE, '%y%m'),'-',@mNUMBER); -- INV-2206-00001
    RETURN mRESULT;
  END
;;
delimiter ;

