-- ----------------------------
-- Function structure for fn_time_ago
-- ----------------------------
DROP FUNCTION IF EXISTS `fn_time_ago`;
delimiter ;;
CREATE FUNCTION `fn_time_ago`(vDATE VARCHAR(255))
 RETURNS varchar(255) CHARSET latin1 COLLATE latin1_swedish_ci
  DETERMINISTIC
BEGIN
    DECLARE mDATE1 VARCHAR(255) DEFAULT "";
    DECLARE mDATE2 VARCHAR(255) DEFAULT "";     
    
    SELECT UNIX_TIMESTAMP(), UNIX_TIMESTAMP(vDATE) INTO mDATE1, mDATE2;
    SET @second = mDATE1 - mDATE2; 
    SET @minute = ROUND(@second/60);
    SET @hour = ROUND(@second/3600);
    SET @day = ROUND(@second/86400);
    SET @week = ROUND(@second/604800);
    SET @month = ROUND(@second/2419200);
    SET @year = ROUND(@second/29030400);
    
    IF @second <= 60 THEN
      SET @time_ago := CONCAT(@second,' detik yang lalu');
    ELSEIF @minute <= 60 THEN
      SET @time_ago := CONCAT(@minute,' menit yang lalu');
    ELSEIF @hour <= 24 THEN
      SET @time_ago := CONCAT(@hour,' jam yang lalu');      
    ELSEIF @day <= 7 THEN
      SET @time_ago := CONCAT(@day,' hari yang lalu');      
    ELSEIF @week <= 4 THEN  
      SET @time_ago := CONCAT(@week,' minggu yang lalu');     
    ELSEIF @month <= 12 THEN                
      SET @time_ago := CONCAT(@month,' bulan yang lalu');           
    ELSE
      SET @time_ago := CONCAT(@year,' tahun yang lalu');      
    END IF;
    SET @result := @time_ago;
    RETURN @result;
  END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_create_card
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_create_card`;
delimiter ;;
CREATE PROCEDURE `sp_create_card`(IN p_nilai VARCHAR(255),
    IN p_jenis VARCHAR(255),
    IN p_keterangan VARCHAR(255),
    IN p_total INT,
    IN p_awalan VARCHAR(255))
BEGIN
    DECLARE i INT DEFAULT 1;
    DECLARE nomor_urut VARCHAR(10);
    DECLARE nomor_kartu VARCHAR(255);

    WHILE i <= p_total DO
        -- Format nomor urut dengan leading zeros
        SET nomor_urut = LPAD(i, 5, '0');

        -- Gabungkan awalan dengan nomor urut
        SET nomor_kartu = CONCAT(p_awalan,"",nomor_urut);

        -- Insert data ke tabel cards
        VALUES (CONCAT(p_awalan,nomor_kartu), p_jenis, p_keterangan, p_nilai, NOW());

        SET i = i + 1;
    END WHILE;
END
;;
delimiter ;

