INSERT INTO `news` (`news_id`, `news_type`, `news_branch_id`, `news_category_id`, `news_title`, `news_url`, `news_image`, `news_short`, `news_content`, `news_tags`, `news_keywords`, `news_visitor`, `news_user_id`, `news_date_created`, `news_date_updated`, `news_flag`, `news_position`, `news_json`) VALUES (51, 1, 1, 21, 'Cara Memilih Pintu Yang Bagus', 'cara-memilih-pintu-yang-bagus', 'upload/news/20250117105015.webp', '<p>Saat memilih pintu yang bagus, Anda bisa mempertimbangkan beberapa hal, seperti:</p><p>Material pintu</p><p>Ukuran dan proporsi pintu</p><p>Keamanan</p><p>Efisiensi energi</p><p>Warna dan finishing</p><p>Fitur tambahan</p><p>Sirkulasi cahaya dan udara</p>', '<div><div style=\"line-height: 19px;\"><div style=\"\">Saat memilih pintu yang bagus, Anda bisa mempertimbangkan beberapa hal, seperti:</div><ul><li><span style=\"background-color: transparent; font-family: var(--font-family);\">Material pintu</span></li><li>Ukuran dan proporsi pintu</li><li><span style=\"background-color: transparent; font-family: var(--font-family);\">Keamanan</span></li><li>Efisiensi energi</li><li>Warna dan finishing</li><li>Fitur tambahan</li><li>Sirkulasi cahaya dan udara</li></ul><div style=\"\">Berikut adalah beberapa tips memilih pintu yang bagus:</div><ol><li><span style=\"background-color: transparent; font-family: var(--font-family);\">Pilih material pintu yang sesuai dengan kebutuhan, seperti kayu, plastik, atau kaca.</span></li><li>Pertimbangkan desain pintu yang sesuai dengan gaya arsitektur rumah.</li><li>Pertimbangkan ukuran dan proporsi pintu, terutama untuk pintu interior.</li><li>Pertimbangkan keamanan, misalnya dengan memilih pintu logam.</li><li>Pertimbangkan efisiensi energi, misalnya dengan memilih pintu yang hemat energi.</li><li>Pertimbangkan sirkulasi cahaya dan udara, agar ruangan lebih nyaman.</li><li>Pertimbangkan fitur tambahan, misalnya pintu yang ramah disabilitas.</li></ol><div style=\"\">Konsultasikan kebutuhan Anda dengan profesional atau pemasok pintu terpercaya seperti produk yang dikeluarkan oleh Dolphin Door &amp; Window yang di produksi di Kota Semarang</div></div></div><p style=\"line-height: 19px;\"></p><ul></ul><p></p><p></p><ul></ul><p></p><p style=\"color: rgb(248, 248, 242); background-color: rgb(39, 40, 34); font-family: Consolas, &quot;Courier New&quot;, monospace; font-size: 14px; line-height: 19px; white-space: pre;\"><br></p>', NULL, 'undefined', 8, 1, '2025-01-17 10:50:14', '2025-01-21 14:50:41', 0, 1, NULL);
