INSERT INTO `news` (`news_id`, `news_type`, `news_branch_id`, `news_category_id`, `news_title`, `news_url`, `news_image`, `news_short`, `news_content`, `news_tags`, `news_keywords`, `news_visitor`, `news_user_id`, `news_date_created`, `news_date_updated`, `news_flag`, `news_position`, `news_json`) VALUES (64, 1, 1, 21, 'Keamanan Jaringan Wi-fi Di Rumah', 'keamanan-jaringan-wi-fi-di-rumah', 'upload/news/20250317131703.jpg', '<p data-pm-slice=\"1 1 []\">Memastikan jaringan Wi-Fi di rumah tetap aman sangat penting untuk melindungi data pribadi dan menghindari penyalahgunaan jaringan. Berikut beberapa langkah yang bisa dilakukan:</p><ol data-spread=\"false\" start=\"1\"><li><p><strong>Gunakan Kata Sandi yang Kuat</strong>  Hindari password default dan gunakan kombinasi huruf, angka, dan simbol.</p></li><li><p><strong>Nonaktifkan WPS (Wi-Fi Protected Setup)</strong>  Fitur ini bisa menjadi celah keamanan jika tidak dikonfigurasi dengan benar.</p></li><li><p><strong>Gunakan Enkripsi WPA3 atau WPA2</strong>  Pastikan router menggunakan standar enkripsi terbaru untuk keamanan maksimal.</p></li><li><p><strong>Perbarui Firmware Router</strong>  Update perangkat lunak router secara berkala untuk menutup celah keamanan.</p></li><li><p><strong>Filter MAC Address</strong>  Batasi perangkat yang bisa terhubung dengan memfilter berdasarkan MAC Address.</p></li></ol><p>Dengan mengikuti langkah-langkah di atas, jaringan Wi-Fi di rumah bisa lebih aman dan terhindar dari ancaman peretas.</p>', '<p data-pm-slice=\"1 1 []\">Memastikan jaringan Wi-Fi di rumah tetap aman sangat penting untuk melindungi data pribadi dan menghindari penyalahgunaan jaringan. Berikut beberapa langkah yang bisa dilakukan:</p><ol data-spread=\"false\" start=\"1\"><li><p><strong>Gunakan Kata Sandi yang Kuat</strong>  Hindari password default dan gunakan kombinasi huruf, angka, dan simbol.</p></li><li><p><strong>Nonaktifkan WPS (Wi-Fi Protected Setup)</strong>  Fitur ini bisa menjadi celah keamanan jika tidak dikonfigurasi dengan benar.</p></li><li><p><strong>Gunakan Enkripsi WPA3 atau WPA2</strong>  Pastikan router menggunakan standar enkripsi terbaru untuk keamanan maksimal.</p></li><li><p><strong>Perbarui Firmware Router</strong>  Update perangkat lunak router secara berkala untuk menutup celah keamanan.</p></li><li><p><strong>Filter MAC Address</strong>  Batasi perangkat yang bisa terhubung dengan memfilter berdasarkan MAC Address.</p></li></ol><p>Dengan mengikuti langkah-langkah di atas, jaringan Wi-Fi di rumah bisa lebih aman dan terhindar dari ancaman peretas.</p>', NULL, 'undefined', 0, 1, '2025-03-17 10:20:14', '2025-03-17 13:17:03', 1, 1, NULL);
