INSERT INTO `news` (`news_id`, `news_type`, `news_branch_id`, `news_category_id`, `news_title`, `news_url`, `news_image`, `news_short`, `news_content`, `news_tags`, `news_keywords`, `news_visitor`, `news_user_id`, `news_date_created`, `news_date_updated`, `news_flag`, `news_position`, `news_json`) VALUES (105, 0, 1, NULL, 'Karir', 'karir', NULL, NULL, '<p data-start=\"147\" data-end=\"256\"><span style=\"background-color: transparent;\">Kami Membuka Lowongan! Bergabunglah Bersama Kami di Industri Internet Service Provider (ISP)</span></p><p data-start=\"147\" data-end=\"256\">Halo calon profesional hebat!<br><span style=\"background-color: transparent;\">Perusahaan kami yang bergerak di bidang layanan Internet Service Provider (ISP) saat ini sedang mengalami pertumbuhan dan ekspansi yang pesat. Seiring dengan meningkatnya permintaan layanan internet berkualitas tinggi, kami membuka kesempatan bagi Anda yang memiliki semangat kerja tinggi, integritas, dan keinginan untuk berkembang bersama kami.</span></p><p data-start=\"147\" data-end=\"256\">Kami membuka peluang karier untuk posisi tenaga kerja spesialis maupun tenaga administrasi umum, baik yang berpengalaman maupun fresh graduate yang memiliki semangat belajar tinggi.</p><p data-start=\"147\" data-end=\"256\"><span style=\"background-color: transparent;\">Posisi yang Dibutuhkan:</span></p><table class=\"table table-bordered\"><tbody><tr><td><b>Profesi</b></td><td><b>Kualifikasi</b></td></tr><tr><td><span style=\"font-weight: 700; color: rgb(111, 123, 138);\">Network Engineer</span></td><td><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Pengalaman dengan perangkat Mikrotik, Cisco, atau sejenisnya</p><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Menguasai konsep routing, switching, VLAN, PPPoE, dan monitoring jaringan</p><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Bersedia bekerja secara mobile dan shift jika dibutuhkan</p></td></tr><tr><td><span style=\"font-weight: 700; color: rgb(111, 123, 138);\"><br class=\"Apple-interchange-newline\">Technical Support (Lapangan &amp; Helpdesk)</span></td><td><p data-start=\"147\" data-end=\"256\"><span style=\"background-color: transparent;\">Mampu menangani keluhan teknis pelanggan</span></p><p data-start=\"147\" data-end=\"256\"><span style=\"background-color: transparent;\">Mengerti instalasi dan troubleshooting jaringan</span></p><p data-start=\"147\" data-end=\"256\">Komunikatif dan siap bekerja di bawah tekanan</p></td></tr><tr><td><span style=\"color: rgb(111, 123, 138);\"><b>System Administrator</b></span></td><td><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Menguasai Linux server, DNS, DHCP, VPN, firewall</p><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Pengalaman dengan sistem monitoring dan backup</p></td></tr><tr><td><br></td><td><br></td></tr><tr><td><span style=\"color: rgb(111, 123, 138);\"><b>Staff Administrasi</b></span></td><td><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Menguasai Microsoft Office (Word, Excel, dll.)</p><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Teliti, rapi, dan memiliki kemampuan organisasi dokumen yang baik</p><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Komunikatif dan mampu bekerja sama dalam tim</p></td></tr><tr><td><span style=\"color: rgb(111, 123, 138);\"><b>Customer Service</b></span></td><td><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Memiliki kemampuan komunikasi yang baik</p><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Siap melayani pelanggan dengan sopan dan solutif</p><p data-start=\"147\" data-end=\"256\" style=\"color: rgb(111, 123, 138);\">Pengalaman di bidang ISP menjadi nilai tambah</p></td></tr><tr><td><span style=\"color: rgb(111, 123, 138);\"><b>Finance &amp; Billing Staff</b></span></td><td><p data-start=\"147\" data-end=\"256\">Memahami dasar akuntansi dan sistem penagihan</p><p data-start=\"147\" data-end=\"256\">Terbiasa membuat laporan keuangan dan invoice pelanggan</p><p data-start=\"147\" data-end=\"256\">Cekatan dan teliti dalam perhitungan</p></td></tr></tbody></table><p data-start=\"147\" data-end=\"256\"><b>Kualifikasi Umum:</b></p><ul><li>Pria/wanita, usia maksimal 35 tahun</li><li>Pendidikan minimal SMA/SMK (untuk posisi administrasi) dan D3/S1 (untuk posisi teknis/spesialis)</li><li>Siap bekerja penuh waktu</li><li>Mampu bekerja secara individu maupun dalam tim</li><li>Memiliki integritas dan etika kerja yang baik</li></ul><p data-start=\"147\" data-end=\"256\"><b>Mengapa Bergabung dengan Kami?</b></p><ul><li><span style=\"background-color: transparent;\">Suasana kerja yang profesional namun kekeluargaan</span></li><li>Jenjang karier yang terbuka</li><li>Mendukung pengembangan diri dan pelatihan berkala</li><li>Bonus dan insentif menarik</li></ul><p><span class=\"_fadeIn_pfttw_8\"> </span><strong data-start=\"2599\" data-end=\"2638\"><span class=\"_fadeIn_pfttw_8\">Kirimkan </span><span class=\"_fadeIn_pfttw_8\">CV </span><span class=\"_fadeIn_pfttw_8\">dan </span><span class=\"_fadeIn_pfttw_8\">portofolio </span><span class=\"_fadeIn_pfttw_8\">Anda </span><span class=\"_fadeIn_pfttw_8\">ke:</span></strong><span class=\"_fadeIn_pfttw_8\">&nbsp;karir[@]megadata.com</span></p><p><img src=\"https://t3.ftcdn.net/jpg/03/02/67/02/360_F_302670250_MqSGA3iaCt5qDmG6YCceDyxGExqJj7Mv.jpg\" style=\"width: 50%;\"><span class=\"_fadeIn_pfttw_8\"><br></span></p><p><br></p><p></p><div style=\"text-align: justify;\"><br></div>oko<br>k l<br>kml<br><img src=\"https://img.freepik.com/free-photo/multiracial-group-young-creative-people-smart-casual-wear-discussing-business-brainstorming-meeting-ideas-mobile-application-software-design-project-modern-office_7861-3067.jpg?t=st=1746757936~exp=1746761536~hmac=c1d4f60d9ed2c00ded94e4037a36d85a010e59f